import React from "react";
import RoundImage from "react-rounded-image";
import MyAvatar from "./image/avatar.jpg";
import "./index.css";
import cake from "./image/cake-2.gif"
import sfx from "./sound/cmsn.mp3"
import { colorBGTitle, colorTextInBlack } from "./colorDefine";

import strs from "./stringDefine"



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { showLeft: true, menuOpen: false };
    this.onWindowRezie = this.onWindowRezie.bind(this);    
    this.audio = new Audio(sfx);    
    this.audio.volume = 0.05;
    //this.audio.autoplay = true;
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.onWindowRezie)
  }

  componentDidMount() {

    window.addEventListener("resize", this.onWindowRezie, false)
    this.onWindowRezie();
  }
  showSettings(event) {
    event.preventDefault();
  }

  handleStateChange(state) {
    this.setState({ menuOpen: state.isOpen })
  }




  render() {
    return (
      <div>         
        {this.name()}     
        {this.congrate()}         
        {this.birthdayCake()}       
      </div>)

  }

  name() {
    return (<h3 style={textNameStyle}>{"Pé Tâm"}</h3>);
  }
  congrate() {
    return (<h3 style={textCongrateStyle}>{"Sinh Nhật Zui Zẻ"}</h3>);
  }
  birthdayCake(){
    return(
          <img height={500}
          style={cakeStyle}
         src={cake} />
    )
  }

  buttonInfo() {
    return (<div onClick={() => {
       this.setState({ menuOpen: !this.state.menuOpen })
       this.audio.play() }}
      style={{
        marginTop: 10,
        padding: 10,
        background: colorBGTitle,
        color: colorTextInBlack,
        width: 60,
        borderBottomRightRadius: 55,
        borderTopRightRadius: 55,
        cursor: "pointer"
      }}>Info</div>);
  }


  onWindowRezie() {
    var current = [window.outerWidth, window.outerHeight];
    if (current[0] < 800 && this.state.showLeft) {
      this.setState({ showLeft: false });
    } else if (current[0] > 800 && !this.state.showLeft) {
      this.setState({ showLeft: true });
    }
  }
}

const cakeStyle ={
  position: 'absolute', left: '45%', top: '60%',
  transform: 'translate(-50%, -50%)'
}
const textNameStyle = {
  color: '#506D84',
  fontSize:100,
  fontFamily: "font5",
  whiteSpace: "nowrap",
  position: 'absolute', left: '45%', top: '8%',
  transform: 'translate(-50%, -50%)'
}
const textCongrateStyle = {
  color: '#506D84',
  fontSize:120,
  fontFamily: "font5",
  whiteSpace: "nowrap",
  position: 'absolute', left: '45%', top: '22%',
  transform: 'translate(-50%, -50%)'
}



export default App;

var stylesSide = {
  bmBurgerButton: {
    position: 'fixed',
    width: '36px',
    height: '30px',
    left: '36px',
    top: '36px'
  },
  bmBurgerBars: {
    background: '#373a47'
  },
  bmBurgerBarsHover: {
    background: '#a90000'
  },
  bmCrossButton: {
    height: '24px',
    width: '24px'
  },
  bmCross: {
    background: '#bdc3c7'
  },
  bmMenuWrap: {
    position: 'fixed',
    height: '100%'
  },
  bmMenu: {
    background: '#373a47',
    fontSize: '1.15em'
  },
  bmMorphShape: {
    fill: '#373a47'
  },
  bmItemList: {
    color: '',
    padding: '0.8em'
  },
  bmItem: {
    display: 'inline-block'
  },
  bmOverlay: {
    background: 'rgba(0, 0, 0, 0.3)'
  }
}
