import React from "react";
import RoundImage from "react-rounded-image";
import MyAvatar from "./image/avatar.jpg";
import "./index.css";
import GroupName from "./component/groupName"
import LeftLayout from "./component/leftLayout"
import CenterLayout from "./component/centerLayout"
import ScrollUpButton from "react-scroll-up-button"
import { slide as Menu } from 'react-burger-menu'
import cake from "./image/cake-2.gif"
import sfx from "./sound/cmsn.mp3"
import { colorBGTitle, colorTextInBlack } from "./colorDefine";
import ReactAudioPlayer from 'react-audio-player';




class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { showLeft: true, menuOpen: false };
    this.onWindowRezie = this.onWindowRezie.bind(this);
    this.url = "https://drive.google.com/drive/u/0/folders/1DNQTAHlcwKabYzzm3sc2nPEH9tJ0x87L";
    this.audio = new Audio(sfx);    
    this.audio.volume = 0.05;
    this.audio.autoplay = true;
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.onWindowRezie)
  }

  componentDidMount() {

    window.addEventListener("resize", this.onWindowRezie, false)
    this.onWindowRezie();
  }
  showSettings(event) {
    event.preventDefault();
  }

  handleStateChange(state) {
    this.setState({ menuOpen: state.isOpen })
  }




  render() {
    return (
      <div>       
        <iframe src={sfx} allow="autoplay" id="audio" style="display: none"></iframe>
        <audio src={sfx} autoplay loop/>
       {this.birthdayCake()}
       {this.buttonInfo()}
      </div>)

  }

  AudioPlayer () {
    return (
      <audio src={sfx} controls autoPlay={true} volume={0.05}/>
    );
  }
  birthdayCake(){
    return(<div
    style={{

    }}>
          <img height={300}
          style={{
            alignItems:"center",
            flex:1,
            justifyContent:"center",
            alignContent:"center",
            alignSelf:"center"
          }}
         src={cake} />
    </div>)
  }

  buttonInfo() {
    return (<div onClick={() => {
       this.setState({ menuOpen: !this.state.menuOpen })
       this.audio.play() }}
      style={{
        marginTop: 10,
        padding: 10,
        background: colorBGTitle,
        color: colorTextInBlack,
        width: 60,
        borderBottomRightRadius: 55,
        borderTopRightRadius: 55,
        cursor: "pointer"
      }}>Info</div>);
  }


  onWindowRezie() {
    var current = [window.outerWidth, window.outerHeight];
    if (current[0] < 800 && this.state.showLeft) {
      this.setState({ showLeft: false });
    } else if (current[0] > 800 && !this.state.showLeft) {
      this.setState({ showLeft: true });
    }
  }
}



export default App;

var stylesSide = {
  bmBurgerButton: {
    position: 'fixed',
    width: '36px',
    height: '30px',
    left: '36px',
    top: '36px'
  },
  bmBurgerBars: {
    background: '#373a47'
  },
  bmBurgerBarsHover: {
    background: '#a90000'
  },
  bmCrossButton: {
    height: '24px',
    width: '24px'
  },
  bmCross: {
    background: '#bdc3c7'
  },
  bmMenuWrap: {
    position: 'fixed',
    height: '100%'
  },
  bmMenu: {
    background: '#373a47',
    fontSize: '1.15em'
  },
  bmMorphShape: {
    fill: '#373a47'
  },
  bmItemList: {
    color: '',
    padding: '0.8em'
  },
  bmItem: {
    display: 'inline-block'
  },
  bmOverlay: {
    background: 'rgba(0, 0, 0, 0.3)'
  }
}
